#!/usr/bin/env julia

using DelimitedFiles

input = ""

function find_marker(input)
    for i in 1:length(input) - 3
        if length(unique(input[i:i+3])) == 4
            return i+3
        end
    end
    return 0
end

function find_start_marker(input)
    for i in 1:length(input) - 13
        if length(unique(input[i:i+13])) == 14
            return i+13
        end
    end
    return 0
end

@assert(find_marker("mjqjpqmgbljsphdztnvjfqwrcgsmlb") == 7)
@assert(find_marker("bvwbjplbgvbhsrlpgdmjqwftvncz") == 5)
@assert(find_marker("nppdvjthqldpwncqszvftbrmjlhg") == 6)
@assert(find_marker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg") == 10)
@assert(find_marker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw") == 11)

println("part 1 answer = $(find_marker(input))")

@assert(find_start_marker("mjqjpqmgbljsphdztnvjfqwrcgsmlb") == 19)
@assert(find_start_marker("bvwbjplbgvbhsrlpgdmjqwftvncz") == 23)
@assert(find_start_marker("nppdvjthqldpwncqszvftbrmjlhg") == 23)
@assert(find_start_marker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg") == 29)
@assert(find_start_marker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw") == 26)

println("part 2 answer = $(find_start_marker(input))")
